import './styles/presentation.css';

function Presentation() {
    return (
        <div className="presentation">
            <div className="columnLeft">
                <a href="#"><i className="fa fa-github"></i></a>
                <a href="#"><i className="fa fa-linkedin"></i></a>
            </div>
            <div className="columnRight">
                <div className="textColumn">
                    <div className="content">
                        <h1>Alessandro Mariani</h1>
                        <h2>I build things</h2>
                        <p>I am a student of a technical institute studying computer science.I create applications, scripts, tools of any kind and more.</p>
                    </div>
                </div>
                <div className="fotoColumn">
                    <img src="https://marianialessandro.com/profilepicture/alessandromariani" />
                </div>
            </div>
        </div>
    );
}

export default Presentation;
